CREATE TABLE fantasy.fantasy.player (player_id VARCHAR(2147483647) NOT NULL, active VARCHAR(1), college VARCHAR(45), display_name VARCHAR(144) NOT NULL, dob VARCHAR(10), fname VARCHAR(77) NOT NULL, height VARCHAR(5), jersey VARCHAR(3), lname VARCHAR(77) NOT NULL, position VARCHAR(3) NOT NULL, retrieval_year INTEGER NOT NULL, team VARCHAR(3) NOT NULL, weight VARCHAR(3), PRIMARY KEY (player_id))
